<script>import { forwardEventHandlers } from '@threlte/core';
import { loadYoga } from 'yoga-layout';
import InnerFlex from './InnerFlex.svelte';
let yoga;
const initialize = async () => {
    yoga = await loadYoga();
};
initialize();
const component = forwardEventHandlers();
</script>

{#if yoga}
  <InnerFlex
    {yoga}
    {...$$restProps}
    bind:this={$component}
    let:reflow
    let:width
    let:height
  >
    <slot
      {reflow}
      {width}
      {height}
    />
  </InnerFlex>
{/if}
